import React, { useState } from 'react';
import Head from 'next/head'
import styled from 'styled-components'
export default function Blog() {
  const [getPosts, setPosts] = useState([
    { id: '1', image: '/profile-1.jpg', title: 'Paulo Souza ', description: 'vizualizou seu perfil', time: 'hace 15 horas' },
    { id: '2', image: '/profile-2.jpg', title: 'Paulo Souza', description: 'comentou no seu post', time: 'hace 15 horas' },
    { id: '3', image: '/profile-3.jpg', title: 'Pet Shop do Paulo', description: 'vizualizou seu perfil', time: 'hace 15 horas' },
    { id: '4', image: '/profile-1.jpg', title: 'Paulo Souza', description: 'vizualizou seu perfil', time: 'hace 15 horas' },
    { id: '5', image: '/profile-2.jpg', title: 'Paulo Souza', description: 'vizualizou seu perfil', time: 'hace 15 horas' },
    { id: '6', image: '/profile-3.jpg', title: 'Pet Shop do Paulo', description: 'vizualizou seu perfil', time: 'hace 15 horas' },
    { id: '7', image: '/profile-1.jpg', title: 'Paulo Souza', description: 'vizualizou seu perfil', time: 'hace 15 horas' },
    { id: '8', image: '/profile-3.jpg', title: 'Paulo Souza', description: 'vizualizou seu perfil', time: 'hace 15 horas' },
  ]);
  const Title = styled.span`
font-weight: 600
`
const UnorderList = styled.ul`
list-style-type: none;
padding: 0px;
`
const List = styled.li`
display: -webkit-box;
padding: 1rem 1rem;
list-style: none;
border-bottom: 1px solid #CDCDCD;
`
const LeftImage = styled.div`
width: 3rem;
grid-column: auto;
-m-flex-preferred-size: auto;
flex-basis: auto;
webkit-box-flex: 0;
ms-flex-positive: 0;
flex-grow: 0;
ms-flex-negative: 0;
flex-shrink: 0;
ms-grid-row: 1;
grid-row: 1;
margin: 0 1rem 0 0;
`
const Image = styled.img` 
  display: inline-block;
  vertical-align: middle;
  height: 3rem;
  width: 3rem;
  margin: 0 0.5rem 0 0;
  border-radius: 50%;
  max-width: 100%;
`
const Wrapper = styled.div`
 margin : -8px;
`;
const UserContent = styled.div`
-ms-flex-preferred-size: auto;
-flex-basis: auto;
-webkit-box-flex: 1;
ms-flex-positive: 1;
flex-grow: 1;
ms-flex-negative: 1;
flex-shrink: 1;
ms-grid-row: 1;
grid-row: 1;
transform: translate(5p 5px);
grid-column: span 9;
`;
  return (
    <Wrapper>
      <Head>
        <title>My page title</title>
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        <script src="https://unpkg.com/styled-components/dist/styled-components.min.js"></script>
      </Head>
      <UnorderList >
        {getPosts.map(post => (
          <List key={post.id} >
            <LeftImage >
              <Image
                src={post.image} style={userPhoto} />
            </LeftImage >
            <UserContent>
              <p style={userInfo}>
                <Title>
                  {post.title}
                </Title> {post.description}</p>
              <p style={timeStyle}>{post.time}</p>
            </UserContent>
          </List>
        ))}
      </UnorderList>

    </Wrapper>
  );
}

const ulStyle = {
  listStyleType: 'none',
  padding: '0px'
};
const listStyle = {
  display: '-webkit-box',
  padding: '1rem 1rem',
  listStyle: 'none',
  borderBottom: '1px solid #CDCDCD',
}
const imgLeft = {
  width: '3rem',
  gridColumn: 'auto',
  mFflexPreferredSize: 'auto',
  flexBasis: 'auto',
  webkitBoxFlex: '0',
  msFlexPositive: '0',
  flexGrow: '0',
  msFlexNegative: '0',
  flexShrink: '0',
  msGridRow: '1',
  gridRow: '1',
  margin: '0 1rem 0 0',
}
const userPhoto = {
  display: 'inline-block',
  verticalAlign: 'middle',
  height: '3rem',
  width: '3rem',
  margin: '0 0.5rem 0 0',
  borderRadius: '50%',
  maxWidth: '100%',
}
const userContent = {
  msFlexPreferredSize: 'auto',
  flexBasis: 'auto',
  webkitBoxFlex: '1',
  msFlexPositive: '1',
  flexGrow: '1',
  msFlexNegative: '1',
  flexShrink: '1',
  msGridRow: '1',
  gridRow: '1',
  transform: 'translate(5px, 5px)',
  gridColumn: 'span 9',
}
const userInfo = {
  margin: '0.15rem 0 0'
}
const nameStyle = {
  fontWeight: '600',
}
const timeStyle = {
  margin: '0',
  color: '#9da4ae'
}
